extern crate bincode;
extern crate serde;

use bellman::{Circuit, ConstraintSystem, SynthesisError};
use bellman::groth16::{PreparedVerifyingKey, VerifyingKey};
use pairing::bls12_381::Bls12;
use pairing::{CurveAffine, Engine};

use sapling_crypto::circuit::num::AllocatedNum;
use sapling_crypto::circuit::pedersen_hash;
use sapling_crypto::jubjub::JubjubEngine;

use self::bincode::Error;
use crate::serialization::objects::*;
use std::io;

pub fn serialize(
    vk: &VerifyingKey<Bls12>
) -> Vec<u8>
{
    bincode::serialize(&Bls12PreparedVerifyingKey::from_groth16(vk)).unwrap()
}

pub fn deserialize(
    bytes: Vec<u8>
) -> io::Result<PreparedVerifyingKey<Bls12>>
{
    let r: bincode::Result<Bls12PreparedVerifyingKey> = bincode::deserialize(bytes.as_slice());
    r.map(|vk| Bls12PreparedVerifyingKey::to_groth16(vk)).map_err(|e| io::Error::from(io::ErrorKind::InvalidData))
}

#[cfg(test)]
mod test {
    use bellman::groth16::{generate_random_parameters, Proof};
    use rand::os::OsRng;
    use rand::Rng;

    use sapling_crypto::jubjub::JubjubBls12;

    use super::*;
    use base64;
    use std::ptr::eq;

    #[derive(Clone)]
    pub struct PedersenDemo<E: JubjubEngine> {
        pub params: Box<E::Params>,
        pub image: Option<E::Fr>,
        pub preimage: Option<E::Fr>,
    }

    impl<E: JubjubEngine> Circuit<E> for PedersenDemo<E> {
        fn synthesize<CS: ConstraintSystem<E>>(
            self,
            cs: &mut CS,
        ) -> Result<(), SynthesisError>
        {
            let image = AllocatedNum::alloc(cs.namespace(|| "signal public input image"), || self.image.ok_or(SynthesisError::AssignmentMissing))?;
            image.inputize(cs.namespace(|| "image inputize"));
            let preimage = AllocatedNum::alloc(cs.namespace(|| "signal input preimage"), || self.preimage.ok_or(SynthesisError::AssignmentMissing))?;
            let preimage_bits = preimage.into_bits_le_strict(cs.namespace(|| "preimage_bits <== bitify(preimage)"))?;
            let image_calculated = pedersen_hash::pedersen_hash(
                cs.namespace(|| "image_calculated <== pedersen_hash(preimage_bits)"),
                pedersen_hash::Personalization::NoteCommitment,
                &preimage_bits,
                &self.params,
            )?.get_x().clone();
            cs.enforce(|| "image_calculated === image", |lc| lc + image.get_variable(), |lc| lc + CS::one(), |lc| lc + image_calculated.get_variable());
            Ok(())
        }
    }

    #[test]
    fn test_serialize_verifying_key() {
        let rng = &mut OsRng::new().unwrap();

        let params = {
            let c = PedersenDemo::<Bls12> {
                params: Box::new(JubjubBls12::new()),
                image: None,
                preimage: None,
            };
            generate_random_parameters(c, rng).unwrap()
        };

        // Prepare the verification key (for proof verification)
        let r = serialize(&params.vk);

//        println!("{}", base64::encode(r.as_slice()));

        assert_eq!(r.len(), 39678);
    }

    #[test]
    fn test_deserialize_verifying_key() {
        let vec = base64::decode(&"").unwrap();
        let bytes = vec.as_slice();

        // Prepare the verification key (for proof verification)

        let r = deserialize(Vec::from(bytes));

        let bincode_res: Bls12PreparedVerifyingKey = bincode::deserialize(bytes).unwrap();

        // first data element
        assert_eq!(bincode_res.alpha_g1.x.0[0], 15688981621134891877);
        // last data element
        assert_eq!(bincode_res.ic[1].x.0[5], 704911249765391873);
    }
}